        margin-left: 0.7rem;
        @include flex-center;
        width: 30px;
        height: 30px;
        &:hover {
          background-color: rgba(51, 51, 51, 0.49);
          cursor: pointer;
        }
      }
      .next-arrow {
        margin-right: 0.7rem;
        @include flex-center;
        width: 30px;
        height: 30px;
        &:hover {
          background-color: rgba(51, 51, 51, 0.49);
          cursor: pointer;
        }
      }
    }
    .primary-nav {
      position: absolute;
      top: 0;
      left: 0;
      display: inline-flex;
      margin-top: 3rem;
      margin-left: 3.5rem;
      width: 70%;
      z-index: 100%;
      .logo-div {
        aspect-ratio: 1 / 1;
        max-width: 5rem;
        max-height: 5rem;
        margin-right: 2rem;
      }
      .nav-list {
        margin-left: 3rem;
        display: flex;
        justify-content: space-between;
        width: 70%;
        .nav-item {
          text-decoration: none;
          color: white;
          text-transform: uppercase;
          transition: 0.5s all ease;
          font-family: $ff-ph;
          font-size: 0.8rem;
          letter-spacing: -1px;
          line-height: 14px;
          display: flex;
          flex-direction: column;
          &:hover {
            color: #e3e3e3;
            &::after {
              display: block;
              transition: all 0.5s ease;
              transition-delay: 0.5s;
            }
          }
          &::after {
            content: "";
            color: white;
            position: relative;
            width: 100%;
            height: 2px;
            background-color: white;
            margin: 0 auto;

            display: none;
          }
        }
      }
    }
  }
}
